import React from 'react';
import './App.css';

const BigSquare = () => {
  const renderSmallSquare = (key, isBlack) => {
    const color = isBlack ? 'black' : 'white';
    return <div key={key} className={`small-square ${color}`}></div>;
  };

  const renderRow = (rowIndex) => {
    const smallSquares = [];
    const isRowEven = rowIndex % 2 === 0;
    for (let i = 0; i < 8; i++) {
      smallSquares.push(renderSmallSquare(i, (i + isRowEven) % 2 === 0));
    }
    return <div key={rowIndex} className="small-row">{smallSquares}</div>;
  };

  const renderGrid = () => {
    const rows = [];
    for (let i = 0; i < 8; i++) {
      rows.push(renderRow(i));
    }
    return <div className="big-square">{rows}</div>;
  };

  return <div className="big-square-container">{renderGrid()}</div>;
};

const App = () => {
  return (
    <div className="App">
      <BigSquare />
    </div>
  );
};

export default App;
