import React, { useState } from 'react';
import './App.css';

const Board = () => {
  const [board, setBoard] = useState([
    ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r'],
    ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
    Array(8).fill(null),
    Array(8).fill(null),
    Array(8).fill(null),
    Array(8).fill(null),
    ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
    ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R'],
  ]);

  const renderSquare = (piece, row, col) => {
    return (
      <div key={`${row}-${col}`} className="square">
        {piece}
      </div>
    );
  };

  const renderRow = (row, rowIndex) => {
    return (
      <div key={rowIndex} className="board-row">
        {row.map((piece, colIndex) => renderSquare(piece, rowIndex, colIndex))}
      </div>
    );
  };

  return (
    <div>
      {board.map((row, index) => renderRow(row, index))}
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <Board />
    </div>
  );
};

export default App;
